 if ((originalLte - originalGte) < 86400000) { 
 return { gte, lte };
 }

 const startOfGteDay = new Date(originalGte);
 startOfGteDay.setUTCHours(0, 0, 0, 0);
 const adjustedGte = startOfGteDay.getTime();

 const endOfLteDay = new Date(originalLte);
 endOfLteDay.setUTCHours(23, 59, 59, 999);
 const adjustedLte = endOfLteDay.getTime();

 return { gte: adjustedGte, lte: adjustedLte };})}

