properties.definition:
${((inputData) => { if (!inputData || !inputData.session || !Array.isArray(inputData.session) || !inputData.users || !Array.isArray(inputData.users) || inputData.session.length !== inputData.users.length) {
 throw new Error('Invalid input data structure');
 }

 const transformedData = inputData.session.map((sessionEntry, index) => {
 const userEntry = inputData.users[index];
 if (sessionEntry.date_ms !== userEntry.date_ms) {
 throw new Error('Session and users data have mismatched dates');
 }
 const CFS = ((sessionEntry.count - sessionEntry.crashing) / sessionEntry.count) * 100;
 const CFU = ((userEntry.count - userEntry.crashing) / userEntry.count) * 100;
 return {
 date: sessionEntry.date_ms,
 CFS: CFS,
 CFU: CFU
 };
 }).sort((a, b) => new Date(a.date) - new Date(b.date));

 return transformedData;})}

${((overtimeData) => { if (overtimeData.length === 0) {
 return { min: null, max: null };
 }

 const allValues = [];

 overtimeData.forEach(d => {
 if (typeof d.CFS === 'number' && !isNaN(d.CFS)) {
 allValues.push(d.CFS);
 }
 if (typeof d.CFU === 'number' && !isNaN(d.CFU)) {
 allValues.push(d.CFU);
 }
 });

 if (allValues.length === 0) {
 return { min: null, max: null };
 }

 const min = Math.min(...allValues);
 const max = Math.max(...allValues);

 return { min, max };})}

